import { useState } from "react";
import React from 'react';

import Autocomplete from "react-google-autocomplete";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col } from 'react-bootstrap';

function ODDCal() {
  const [origin, setOrigin] = useState('');
  const [destination, setDestination] = useState('');
  const [data, setData] = useState({});
  const APIKey = 'cZrRhJKgF0Qe4wjI2D5EvJ93mSX0x'
  const url = 'https://api.distancematrix.ai/maps/api/distancematrix/json?'

  const logOut = () => {
    localStorage.removeItem('user');
    window.location.reload(false);
  }

  async function fetchMoviesJSON() {
    const response = await fetch(url+'origins='+origin+'&destinations='+destination+'&key='+APIKey);
    const movies = await response.json();
    return movies;
  }

  const findDist = () => {
    fetchMoviesJSON()
    .then(movies => {
      console.log(movies); // fetched movies
    })
    .catch(error => console.log(error))
  }

  return (
    <Container style={{'height':'100vh'}}>
      <Row>
        <Col sm={3} style={{'backgroundColor':'blue'}}>
          <input type="text" onChange={(e)=>{setOrigin(e.target.value)}} placeholder="Enter Origin"></input>
          <input type="text" onChange={(e)=>{setDestination(e.target.value)}} placeholder="Enter Destination"></input>

          <Row>
          <Col>
            <button onClick={findDist}>Submit</button>
          </Col>
          <Col>
            <button onClick={logOut}>LogOut</button>
          </Col>
          </Row>
        </Col>
        <Col sm={9} style={{'backgroundColor':'gray'}}>2 of 3</Col>
      </Row>
    </Container>
    // <div>
    //     This is O-D Distance Calculator !!!.
        // <button onClick={logOut}>LogOut</button>
        
        // <input type="text" onChange={(e)=>{setOrigin(e.target.value)}} placeholder="Enter Origin"></input>
        // <input type="text" onChange={(e)=>{setDestination(e.target.value)}} placeholder="Enter Destination"></input>

        // <button onClick={findDist}>Submit</button>
    // </div>
  );
}

export default ODDCal;
