import { useState } from "react";
import React from 'react';

import Autocomplete from "react-google-autocomplete";

function ODDCal() {
  const [origin, setOrigin] = useState('');
  const [destination, setDestination] = useState('');
  const [data, setData] = useState({});
  const APIKey = 'AIzaSyBILV7Ujs5XOJbynyKoJCIOs437KT2CxdM'
  const url ='https://maps.googleapis.com/maps/api/distancematrix/json?'

  const logOut = () => {
    localStorage.removeItem('user');
    window.location.reload(false);
  }

  const findDist = () => {
    console.log(origin, destination);

    fetch(url+'origins = '+origin+'&destinations = '+destination+'&key = '+APIKey)
    .then((response) => {
      // indicates whether the response is successful (status code 200-299) or not
      // if (!response.ok) {
      //   throw new Error(`Request failed with status ${reponse.status}`)
      // }
      setData(response.json());
      // return response.json()
    })
    .then(data => {
      console.log(data)
    })
    .catch(error => console.log(error))
  }

  return (
    <div>
        This is O-D Distance Calculator !!!.
        <button onClick={logOut}>LogOut</button>
        
        <input type="text" onChange={(e)=>{setOrigin(e.target.value)}} placeholder="Enter Origin"></input>
        <input type="text" onChange={(e)=>{setDestination(e.target.value)}} placeholder="Enter Destination"></input>

        <button onClick={findDist}>Submit</button>
    </div>
  );
}

export default ODDCal;
