import { useState } from "react";
import React from 'react';

import {firebase} from './FireBase';
import { getAuth, RecaptchaVerifier, signInWithPhoneNumber  } from "firebase/auth";
import './PhoneAuth.css'
import CardElement from './Card';


function PhoneAuth() {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [OTP, setOTP] = useState('');
  const [show, setShow] = useState(false);
  const [finalResult, setFinalResult] = useState('');

  const signIn = () => {
    console.log('this is signIn !!!', phoneNumber);
    if(phoneNumber.length === 13){
      const auth = getAuth();
      let appVerifier = new RecaptchaVerifier('recaptcha', {}, auth);
      
      signInWithPhoneNumber(auth, phoneNumber, appVerifier).then((result)=>{
        setFinalResult(result);
        alert('OTP Sent !!!');
        setShow(true);
      }).catch((error)=>{
        alert(error);
      })
    }
    return;
  }

  const verifyOTP = () => {
    // console.log('this is the verify OTP !!!');
    if( finalResult!=null && OTP!=null ){
      finalResult.confirm(OTP).then((result)=>{
        // Success
        console.log('user loggedIn > ', phoneNumber)
        localStorage.setItem('user', phoneNumber)
        window.location.reload(false);
      }).catch((error)=>{
        alert("Wrong Code");
      })
    }
    return ;
  }

  return (
    // <div>
    //   <div>
    //     <input value={phoneNumber} onChange={(e)=>{setPhoneNumber(e.target.value)}} placeholder="Enter your Phone-number !!!" />
    //     <div id="recaptcha"></div>
    //     <button onClick={signIn}>Send OTP</button>
    //   </div>
    //   <div style={{display: show ? "block" : "none"}}>
    //     <input type="text" onChange={(e)=>{setOTP(e.target.value)}} placeholder="Enter your OTP here"></input>
    //     <button onClick={verifyOTP}>Verify</button>
    //   </div>
    // </div>

    <div className="page-container-PhoneAuth">
      <div className="container-left-PhoneAuth">
        <div className="pageTitle-PhoneAuth">
            Welcome to <u>IISc CiSTUP 2022 Summer Internship Program</u> <br></br>Task Round
        </div>
        <div className="pageBody-PhoneAuth">
            <b>Aditya R. Chakole</b> <br></br>
            Pre-Final Year Student at IIT Kharagpur
        </div>
      </div>
      <div className="container-right-PhoneAuth">
        <CardElement />
      </div>
    </div>
  );
}

export default PhoneAuth;
