import { useState } from "react";
import React from 'react';

import Autocomplete from "react-google-autocomplete";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col, Button } from 'react-bootstrap';
import './ODDCal.css'

function ODDCal() {
  const [origin, setOrigin] = useState('');
  const [destination, setDestination] = useState('');
  const [data, setData] = useState({});
  const APIKey = 'cZrRhJKgF0Qe4wjI2D5EvJ93mSX0x'
  const url = 'https://api.distancematrix.ai/maps/api/distancematrix/json?'

  const logOut = () => {
    localStorage.removeItem('user');
    window.location.reload(false);
  }

  async function fetchMoviesJSON() {
    const response = await fetch(url+'origins='+origin+'&destinations='+destination+'&key='+APIKey);
    const movies = await response.json();
    return movies;
  }

  const findDist = () => {
    fetchMoviesJSON()
    .then(movies => {
      console.log(movies); // fetched movies
    })
    .catch(error => console.log(error))
  }

  return (
    <div className="page-container">
      <div className="container-left">

      <form>
        <div class="form-group">
          <label for="exampleInputEmail1">Email address</label>
          <input style={{'margin':'5px'}} type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email"/>
          <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
        </div>
        <div class="form-group">
          <label for="exampleInputPassword1">Password</label>
          <input type="password" class="form-control" id="exampleInputPassword1" placeholder="Password" />
        </div>
        <div class="form-check">
          <input type="checkbox" class="form-check-input" id="exampleCheck1" />
          <label class="form-check-label" for="exampleCheck1">Check me out</label>
        </div>
        <button type="submit" class="btn btn-primary">Submit</button>
      </form>

        {/* <input type="text" style={{'margin':'5px'}} onChange={(e)=>{setOrigin(e.target.value)}} placeholder="Enter Origin"></input>
        <input type="text" style={{'margin':'5px'}} onChange={(e)=>{setDestination(e.target.value)}} placeholder="Enter Destination"></input>

        <Button style={{'margin':'5px'}} onClick={findDist}>Submit</Button>
        <Button style={{'margin':'5px'}} onClick={logOut}>LogOut</Button> */}
      </div>
      <div className="container-right">
        this is right
      </div>
    </div>
  );
}

export default ODDCal;
