import { useState } from "react";
import React from 'react';


import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col, Button } from 'react-bootstrap';
import './ODDCal.css'

function EnumeratorMonitoring() {
  const [data, setData] = useState({});
  const [date, setDate] = useState("2022-04-03");
  
  const URL = "https://oshr.cistup.iisc.ac.in:443/enumstatus"
  var headers = new Headers();
  headers.append("Content-Type", "application/json");
  var requestOptions = {
    method: 'POST',
    headers: headers,
    body: JSON.stringify(date),
    redirect: 'follow'
  };

  const logOut = () => {
    localStorage.removeItem('user');
    window.location.reload(false);
  }

  const fetchData = () => {
    fetch(URL, requestOptions)
    .then(response => response.text())
    .then(result => console.log(result))
    .catch(error => console.log('error', error));
  }

  const handleSubmit = () => {
      console.log('>>>>>>> ', date);
  }

  return (
    <div className="page-container">
      <div className="container-left">
        <input type="date" id="date" defaultValue={date} onChange={(e)=>{setDate(e.target.value)}} />
        <input type="submit" onClick={handleSubmit}/>
      </div>
      <div className="container-right">
        this is right
      </div>
    </div>
  );
}

export default EnumeratorMonitoring;
