import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";

import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, Container, Nav, NavDropdown } from 'react-bootstrap';

import PhoneAuth from './components/PhoneAuth'
import ODDCal from './components/ODDCal'
import EnumeratorMonitoring from './components/EnumeratorMonitoring'
import './App.css';

function App() {
  const [user, setUser] = useState('');

  useEffect(()=>{
    const loggedInUser = localStorage.getItem("user");
    if( loggedInUser ){
      console.log(loggedInUser);
      setUser(loggedInUser);
    }
  }, []);

  return (
    <>
      {/* <Navbar bg="dark" variant="dark">
        <Container>
        <Navbar.Brand href="/">IISc CiSTUP</Navbar.Brand>
        <Nav >
          <Nav.Link href="/ODDistanceCalculator">O-D Distance Calculator</Nav.Link>
          <Nav.Link href="/EnumeratorMonitoring">Enumerator Monitoring</Nav.Link>
        </Nav>
        </Container>
      </Navbar> */}

      <Navbar bg="light" expand="lg">
        <Container>
          <Navbar.Brand href="#home">React-Bootstrap</Navbar.Brand>
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              <Nav.Link href="#home">Home</Nav.Link>
              <Nav.Link href="#link">Link</Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      <Router>
        <Routes>
          <Route path="/" caseSensitive={false} element={ user ? <>WelCome</> : <PhoneAuth />} />
          <Route path="/ODDistanceCalculator" caseSensitive={false} element={user ? <ODDCal /> : <PhoneAuth />} />
          <Route path="/EnumeratorMonitoring" caseSensitive={false} element={user ? <EnumeratorMonitoring /> : <PhoneAuth />} />
        </Routes>
      </Router>

      <Navbar bg="dark" variant="dark" fixed="bottom" style={{"height": "30px"}}>
        <Container>
        <Nav className="mx-auto">
          <Nav.Link href="/ODDistanceCalculator">Project sponsored by <b style={{"color":"white"}}>MeitY</b></Nav.Link>
        </Nav>
        </Container>
      </Navbar>
    </>
  );
}

export default App;
