import { useState } from "react";
import React from 'react';

import Autocomplete from "react-google-autocomplete";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col, Button } from 'react-bootstrap';
import './ODDCal.css'

function ODDCal() {
  const [origin, setOrigin] = useState('');
  const [destination, setDestination] = useState('');
  const [data, setData] = useState({});
  const APIKey = 'cZrRhJKgF0Qe4wjI2D5EvJ93mSX0x'
  const url = 'https://api.distancematrix.ai/maps/api/distancematrix/json?'

  const logOut = () => {
    localStorage.removeItem('user');
    window.location.reload(false);
  }

  async function fetchMoviesJSON() {
    const response = await fetch(url+'origins='+origin+'&destinations='+destination+'&key='+APIKey);
    const movies = await response.json();
    return movies;
  }

  const findDist = () => {
    fetchMoviesJSON()
    .then(movies => {
      console.log(movies); // fetched movies
    })
    .catch(error => console.log(error))
  }

  return (
    <div className="page-container">
      <div className="container-left">

      <form>
        <div class="form-group">
          <label for="exampleInputEmail1">Origin</label>
          <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter Origin"/>
        </div>
        <div class="form-group">
          <label for="exampleInputPassword1">Destination</label>
          <input type="password" class="form-control" id="exampleInputPassword1" placeholder="Enter Destination" />
        </div>
        <br></br>
        
        <button type="submit" class="btn btn-primary">Submit</button>
        <br></br>
        <br></br>
        <button class="btn btn-primary" onClick={logOut}>LogOut</button>
      </form>

        {/* <input type="text" style={{'margin':'5px'}} onChange={(e)=>{setOrigin(e.target.value)}} placeholder="Enter Origin"></input>
