        <Button style={{'margin':'5px'}} onClick={findDist}>Submit</Button>
        <Button style={{'margin':'5px'}} onClick={logOut}>LogOut</Button> */}
      </div>
      <div className="container-right">
        this is right
      </div>
    </div>
  );
}

export default ODDCal;
