import { useState } from "react";
import React from 'react';

import Calendar from 'react-calendar';

import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col, Button } from 'react-bootstrap';
import './ODDCal.css'

function EnumeratorMonitoring() {
  const [origin, setOrigin] = useState('');
  const [destination, setDestination] = useState('');
  const [data, setData] = useState({});
  const [date, setDate] = useState({"input_date":"2022-04-03"});
  const [value, onChange] = useState(new Date());
  
  const URL = "https://oshr.cistup.iisc.ac.in:443/enumstatus"
  var headers = new Headers();
  headers.append("Content-Type", "application/json");
  var requestOptions = {
    method: 'POST',
    headers: headers,
    body: JSON.stringify(date),
    redirect: 'follow'
  };

  const logOut = () => {
    localStorage.removeItem('user');
    window.location.reload(false);
  }

  const fetchData = () => {
    fetch(URL, requestOptions)
    .then(response => response.text())
    .then(result => console.log(result))
    .catch(error => console.log('error', error));
  }

  return (
    <div className="page-container">
      <div className="container-left">
        {value ? 0 : value}
        <Calendar onChange={onChange(value)} value={value} />
      </div>
      <div className="container-right">
        this is right
      </div>
    </div>
  );
}

export default EnumeratorMonitoring;
