import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";

import PhoneAuth from './components/PhoneAuth'
import ODDCal from './components/ODDCal'
import './App.css';

function App() {
  const [user, setUser] = useState('');

  useEffect(()=>{
    const loggedInUser = localStorage.getItem("user");
    if( loggedInUser ){
      console.log(loggedInUser);
      setUser(loggedInUser);
    }
  }, []);

  return (
    <>
      <Router>
        <Routes>
          <Route path="/" caseSensitive={false} element={ user ? <>WelCome</> : <>Home</>} />
          <Route path="/login" caseSensitive={false} element={user ? <>WelCome</> : <>Login</>} />
          <Route path="/register" caseSensitive={false} element={user ? <>WelCome</> : <>Register</>} />
        </Routes>
      </Router>
    </>
    // <div className="App">
    //   HLO
    //   <PhoneAuth />
    //   {/* <ODDCal /> */}
    // </div>
  );
}

export default App;
